import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // Error message

  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Reset error message before submitting

    try {
      const response = await axios.post("http://localhost:4000/login", {
        email,
        password,
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
    if (response.ok) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('userName', data.user.name);
        setLoggedInUser(data.user.name); // Update state if needed
    } else {
        console.error(data.message);
    }

      if (response.status === 200) {
        console.log("Login successful:", response.data);
        setError(""); // Clear any existing error

        // Store the authentication token in localStorage
        localStorage.setItem("authToken", response.data.token); // Store token

        // Redirect to the Dashboard
        navigate("/dashboard");
      }
    } catch (error) {
      console.error("Login error:", error);
      setError(error.response?.data?.message || "Login failed");
    }
  };

  return (
    <div className="login-container">
      <div className="login-left">
        <img src="wallpaper.png" alt="Background" className="login-image" />
      </div>
      <div className="login-right">
        <div className="top-button">
          <button className="register-button" onClick={() => navigate("/signup")}>
            SignUp
          </button>
          <button className="login-button">Login</button>
        </div>
        <div className="login-form">
          <h2 className="login-text">Login</h2>

          <form onSubmit={handleLogin}>
            <div className="form-group">
              <input
                type="email"
                id="email"
                placeholder="Email id"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              {error && <p className="error-message">{error}</p>}
            </div>

            <div className="form-group">
              <input
                type="password"
                id="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              {error && <p className="error-message">{error}</p>}
            </div>

            <button type="submit" className="login-btn">
              Login
            </button>
          </form>

          <p>
            Don't have an account?{" "}
            <button className="sig" onClick={() => navigate("/signup")}>
              SignUp
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
