import React, { useState, useEffect } from "react";
import "./Dashpart.css"; // Optional CSS file for styling
import axios from 'axios';

const Dashpart = () => {
  const [dateWiseClicks, setDateWiseClicks] = useState([]);
  const [clickDevices, setClickDevices] = useState([]);
  const [totalClicks, setTotalClicks] = useState(0);

  // Fetch data from the API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:4000/api/get-click-data"); // API endpoint
        setDateWiseClicks(response.data.dateWiseClicks); // Set date-wise clicks data
        setClickDevices(response.data.clickDevices); // Set device-wise clicks data
        setTotalClicks(response.data.totalClicks); // Set total clicks
      } catch (error) {
        console.error("Error fetching click data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="dashpart-container">
      <h1>Total Clicks: <span className="total-clicks">{totalClicks}</span></h1>
      <div className="dashpart-grid">
        {/* Date-wise Clicks */}
        <div className="dashpart-card">
          <div className="chart-section">
            <h2>Date-wise Clicks</h2>
            <ul>
              {dateWiseClicks.map((data, index) => (
                <li key={index} className="date-row">
                  <span className="date">{data.date}</span>
                  <div className="bar" style={{ width: `${data.clicks / 10}px` }}></div>
                  <span className="clicks">{data.clicks}</span>
                </li>
              ))}
            </ul>
          </div>
        </div>

        {/* Click Devices */}
        <div className="dashpart-card">
          <div className="chart-section">
            <h2>Click Devices</h2>
            <ul>
              {clickDevices.map((data, index) => (
                <li key={index} className="device-row">
                  <span className="device">{data.device}</span>
                  <div className="bar" style={{ width: `${data.clicks * 5}px` }}></div>
                  <span className="clicks">{data.clicks}</span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashpart;
