import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(''); // To store error message

    const navigate = useNavigate();

    const handleClick = () => {
        navigate("/signup");
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        setError(""); // Reset error message before submitting

        try {
            const response = await axios.post("http://localhost:4000/login", {
                email,
                password
            });

            if (response.status === 200) {
                console.log("Login successful:", response.data);

                // Store the token in localStorage (or sessionStorage)
                localStorage.setItem('authToken', response.data.token);

                // Redirect to dashboard or home page
                navigate("/dashboard");
            }
        } catch (error) {
            console.error("Error during login:", error.response?.data || error.message);
            setError(error.response?.data?.message || "Login failed");
        }
    };

    return (
        <div className="login-container">
            <div className="login-left">
                <img src="wallpaper.png" alt="Background" className="login-image" />
            </div>
            <div className="login-right">
                <div className="top-button">
                    <button className="register-button" onClick={handleClick}>SignUp</button>
                    <button className="login-button">Login</button>
                </div>
                <div className="login-form">
                    <h2 className="login-text">Login</h2>
                    {error && <p className="error-message">{error}</p>}
                    <form onSubmit={handleLogin}>
                        <div className="form-group">
                            <input
                                type="email"
                                id="email"
                                placeholder="Email id"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <input
                                type="password"
                                id="password"
                                placeholder="Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <button type="submit" className="login-btn">Login</button>
                    </form>
                    <p>Don't have an account? <button className="sig" onClick={handleClick}>SignUp</button></p>
                </div>
            </div>
        </div>
    );
};

export default Login;
