import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Data.css';

const Data = () => {
  const [links, setLinks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchLinks = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const response = await axios.get('http://localhost:4000/api/get-links', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setLinks(response.data.links);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
      }
    };

    fetchLinks();
  }, []);

  return (
    <div className="data-container">
      <div className="table-header">
        <ul>
          <li>Date</li>
          <li>Original Link</li>
          <li>Short Link</li>
          <li>Remarks</li>
          <li>Clicks</li>
          <li>Status</li>
          <li>Actions</li>
        </ul>
      </div>
      <div className="table-body">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          links.map((link, index) => (
            <ul key={index}>
              <li>{new Date(link.createdAt).toLocaleString()}</li>
              <li>{link.destinationUrl}</li>
              <li>{link.shortUrl}</li>
              <li>{link.remarks}</li>
              <li>{link.clicks}</li>
              <li>{link.expirationDate ? 'Active' : 'Inactive'}</li>
              <li>
                <button>Edit</button>
                <button>Delete</button>
              </li>
            </ul>
          ))
        )}
      </div>
    </div>
  );
};

export default Data;
