import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Setting.css';

const Setting = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: ''
    });

    const [loading, setLoading] = useState(false);

    // Fetch user details on component mount
    useEffect(() => {
        const fetchUserDetails = async () => {
            try {
                const token = localStorage.getItem('token'); // Adjust based on your auth flow
                const response = await axios.get('/api/user-details', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFormData(response.data.user);
            } catch (error) {
                console.error('Error fetching user details:', error);
            }
        };

        fetchUserDetails();
    }, []);

    // Handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    // Handle save changes
    const handleSaveChanges = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const response = await axios.put('/api/user-details', formData, {
                headers: { Authorization: `Bearer ${token}` }
            });
            alert(response.data.message);
        } catch (error) {
            console.error('Error saving changes:', error);
            alert('Failed to save changes');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="setting-container">
            <form className="setting-form">
                <div className="setting-field">
                    <label className="setting-label">Name</label>
                    <input
                        className="setting-input"
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                    />
                </div>
                <div className="setting-field">
                    <label className="setting-label">Email id</label>
                    <input
                        className="setting-input"
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                    />
                </div>
                <div className="setting-field">
                    <label className="setting-label">Mobile no.</label>
                    <input
                        className="setting-input"
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                    />
                </div>
                <button
                    type="button"
                    className="save-button"
                    onClick={handleSaveChanges}
                    disabled={loading}
                >
                    {loading ? 'Saving...' : 'Save Changes'}
                </button>
            </form>
            <div className="delete-button-part">
                <button type="button" className="delete-button">Delete Account</button>
            </div>
        </div>
    );
};

export default Setting;
