import React, { useState, useEffect } from "react";
import "./Navbar.css";
import CreateModal from "./Modals/CreateModal";

const Navbar = () => {
    const [isModalOpen, setModalOpen] = useState(false);
    const [userName, setUserName] = useState('');

    useEffect(() => {
        const storedName = localStorage.getItem('userName');
        console.log('Fetched userName from localStorage:', storedName); // Debugging
        if (storedName) {
            setUserName(storedName);
        }
    }, []);

    const getGreeting = () => {
        const hour = new Date().getHours();
        if (hour < 12) return "🌄 Good morning";
        else if (hour < 18) return "☀️ Good afternoon";
        else return "🌙 Good evening";
    };

    const getFormattedDate = () => {
        const today = new Date();
        const options = { weekday: "short", month: "short", day: "numeric" };
        return today.toLocaleDateString(undefined, options);
    };

    return (
        <>
            <nav className="navbar">
                <div className="navbar-left">
                    <span className="greeting">
                        {`${getGreeting()}, ${userName || 'Guest'}`}
                    </span>
                    <div className="day">
                        <span className="date">{getFormattedDate()}</span>
                    </div>
                </div>
                <div className="navbar-right">
                    <button
                        className="create-button"
                        onClick={() => setModalOpen(true)}
                    >
                        + Create new
                    </button>
                    <input
                        type="text"
                        placeholder="Search by remarks"
                        className="search-input"
                    />
                    <div className="profile-icon">{userName ? userName.charAt(0).toUpperCase() : 'G'}</div>
                </div>
            </nav>

            {/* Modal Component */}
            <CreateModal isOpen={isModalOpen} onClose={() => setModalOpen(false)} />
        </>
    );
};

export default Navbar;
