import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Setting.css';

const Setting = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
  });

  useEffect(() => {
    // Fetch user data on component mount
    
      const fetchUserData = async () => {
        try {
          const response = await axios.get('http://localhost:4000/api/user'); // Correct backend port
          setFormData({
            name: response.data.name,
            email: response.data.email,
            phone: response.data.phone,
          });
        } catch (error) {
          console.error('Error fetching user data:', error);
        }
      };
 

    fetchUserData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = async () => {
    try {
      const response = await axios.put('http://localhost:5000/api/user', formData);
      console.log('Response from server:', response.data);
      alert('Changes saved successfully!');
    } catch (error) {
      console.error('Error saving changes:', error.response?.data || error.message);
      alert('Failed to save changes.');
    }
  };
  
  return (
    <div className="setting-container">
      <form className="setting-form" onSubmit={(e) => e.preventDefault()}>
        <div className="setting-field">
          <label className="setting-label">Name</label>
          <input
            className="setting-input"
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div className="setting-field">
          <label className="setting-label">Email id</label>
          <input
            className="setting-input"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
        <div className="setting-field">
          <label className="setting-label">Mobile no.</label>
          <input
            className="setting-input"
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
          />
        </div>
        <button type="button" className="save-button" onClick={handleSave}>
          Save Changes
        </button>
      </form>
      <div className="delete-button-part">
        <button type="button" className="delete-button">
          Delete Account
        </button>
      </div>
    </div>
  );
};

export default Setting;
