import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Data.css';
import EditModal from './EditModal';
import DeleteModal from "./DeleteModal";

function Data() {
  const [links, setLinks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedLink, setSelectedLink] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [deleteLink, setDeleteLink] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc'); // State for sorting order

  const linksPerPage = 6;

  // Fetch data from the backend
  useEffect(() => {
    const fetchLinks = async () => {
      const token = localStorage.getItem('authToken');
      if (!token) {
        console.error('User is not authenticated');
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/get-links', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setLinks(response.data.links);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();
  }, []);

  // Sort links by date
  const sortByDate = () => {
    const sortedLinks = [...links].sort((a, b) => {
      const dateA = new Date(a.createdAt);
      const dateB = new Date(b.createdAt);
      return sortOrder === 'asc' ? dateA - dateB : dateB - dateA;
    });
    setLinks(sortedLinks);
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  // Filter links by status
  const filterByStatus = () => {
    const currentDate = new Date();
    const filteredLinks = links.filter((link) => {
      const expiry = new Date(link.expiryDate);
      return expiry < currentDate; // Only show expired links
    });
    setLinks(filteredLinks);
  };

  // Shorten URL
  const getShortenedLink = (url) => url.slice(0, 10) + '...';

  // Copy to clipboard
  const copyToClipboard = (shortUrl, index) => {
    navigator.clipboard.writeText(shortUrl).then(() => {
      alert('Short link copied to clipboard!');
      handleLinkClick(shortUrl, index);
    }).catch((error) => {
      console.error('Error copying to clipboard', error);
    });
  };

  // Increment click count
  const handleLinkClick = async (shortUrl, index) => {
    try {
      const encodedShortUrl = encodeURIComponent(shortUrl);
      const response = await axios.get(`http://localhost:4000/api/increment-clicks/${encodedShortUrl}`);

      const updatedLinks = [...links];
      updatedLinks[index].clicks.total = response.data.totalClicks;
      setLinks(updatedLinks);
    } catch (error) {
      console.error('Error incrementing click count:', error);
    }
  };

  // Update link
  const handleLinkUpdate = (updatedLink) => {
    const updatedLinks = links.map((link) =>
      link._id === updatedLink._id ? updatedLink : link
    );
    setLinks(updatedLinks);
  };

  // Delete link
  const handleDeleteConfirm = async () => {
    if (!deleteLink) return;
    try {
      const response = await axios.delete(
        `http://localhost:4000/api/delete-link/${deleteLink._id}`,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
        }
      );

      if (response.status === 200) {
        setLinks(links.filter((link) => link._id !== deleteLink._id));
        setDeleteLink(null);
        alert("Link deleted successfully!");
      }
    } catch (error) {
      console.error("Error deleting link:", error);
    }
  };

  // Get link status
  // Get link status
const getLinkStatus = (expiryDate) => {
  const currentDate = new Date();
  const expiry = new Date(expiryDate);

  if (expiry < currentDate) return 'Inactive'; // Expired link
  const timeDifference = expiry - currentDate;
  const oneDayInMillis = 24 * 60 * 60 * 1000; // 1 day in milliseconds
  return timeDifference <= oneDayInMillis ? 'Expiring Soon' : 'Active'; // Active or Expiring Soon
};

// Update status manually if needed (for example, after modifying links)
const updateLinkStatus = () => {
  const updatedLinks = links.map((link) => ({
    ...link,
    status: getLinkStatus(link.expiryDate),
  }));
  setLinks(updatedLinks);
};

// Call this function to update the status when links change (e.g., after fetching or updating links)
useEffect(() => {
  updateLinkStatus();
}, [links]); // Run when the links state changes


  return (
    <div className="data-container">
      <div className="table-header">
        <ul className="data-table">
          <li>
            Date
            <button className="date-sort" onClick={sortByDate}>
              <img src="sort.png" alt="Sort" />
            </button>
          </li>
          <li>Original Link</li>
          <li>Short Link</li>
          <li>Remarks</li>
          <li>Clicks</li>
          <li>
            Status
            <button className="status-sort" onClick={filterByStatus}>
              <img src="sort.png" alt="Filter" />
            </button>
          </li>
          <li>Action</li>
        </ul>
      </div>

      <div className="table-body">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          currentLinks.map((link, index) => (
            <ul className="data-row" key={index}>
              <li>{new Date(link.createdAt).toLocaleString()}</li>
              <li>
                <a href={link.destinationUrl} target="_blank" rel="noopener noreferrer">
                  {getShortenedLink(link.destinationUrl)}
                </a>
              </li>
              <li>
                <a href={link.shortUrl} target="_blank" rel="noopener noreferrer" onClick={() => handleLinkClick(link.shortUrl, index)}>
                  {getShortenedLink(link.shortUrl)}
                </a>
                <button className="copy" onClick={() => copyToClipboard(link.shortUrl, index)}>
                  <img src="copy.png" alt="Copy" />
                </button>
              </li>
              <li>{link.remarks}</li>
              <li className="count">{link.clicks.total}</li>
              <li className="status">{getLinkStatus(link.expiryDate)}</li>
              <li className="action-icons">
                <button className="edit" onClick={() => setSelectedLink(link)}>
                  <img src="edit.png" alt="Edit" />
                </button>
                <button className="delete" onClick={() => setDeleteLink(link)}>
                  <img src="delete.png" alt="Delete" />
                </button>
              </li>
            </ul>
          ))
        )}
      </div>

      <div className="pagination">
        {pageNumbers.map(number => (
          <button
            key={number}
            onClick={() => handlePageChange(number)}
            className={currentPage === number ? 'active' : ''}
          >
            {number}
          </button>
        ))}
      </div>

      <EditModal
        isOpen={selectedLink !== null}
        onClose={() => setSelectedLink(null)}
        link={selectedLink}
        onUpdate={handleLinkUpdate}
      />

      {deleteLink && (
        <DeleteModal
          onClose={() => setDeleteLink(null)}
          onConfirm={handleDeleteConfirm}
        />
      )}
    </div>
  );
}

export default Data;
