import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Dashpart.css';

function Dashpart() {
  const [totalClicks, setTotalClicks] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Fetch total clicks data
    const fetchClickStats = async () => {
      const token = localStorage.getItem('authToken'); // Assuming JWT token is in localStorage
      if (!token) {
        console.error('User is not authenticated');
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/get-clicks', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setTotalClicks(response.data.totalClicks); // Update total clicks state
        setIsLoading(false); // Stop loading spinner
      } catch (error) {
        console.error('Error fetching click stats:', error);
        setIsLoading(false);
      }
    };

    fetchClickStats();
  }, []);

  return (
    <div className="dashpart-container">
      <div className="click-stats-card">
        <h2>Total Clicks</h2>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <p className="click-count">{totalClicks}</p>
        )}
      </div>
    </div>
  );
}

export default Dashpart;
