import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashpart.css";

function Dashpart() {
  const [totalClicks, setTotalClicks] = useState(0);
  const [deviceClicks, setDeviceClicks] = useState({
    desktop: 0,
    mobile: 0,
    tablet: 0,
  });
  const [dateWiseClicks, setDateWiseClicks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchClickStats = async () => {
      const token = localStorage.getItem("authToken"); // Assuming JWT token is in localStorage
      if (!token) {
        console.error("User is not authenticated");
        return;
      }

      try {
        const response = await axios.get("http://localhost:4000/api/get-clicks", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        // Validate the response data before setting state
        const { totalClicks, deviceClicks, dateWiseClicks } = response.data;

        if (
          deviceClicks &&
          deviceClicks.desktop !== undefined &&
          deviceClicks.mobile !== undefined &&
          deviceClicks.tablet !== undefined
        ) {
          setTotalClicks(totalClicks);
          setDeviceClicks(deviceClicks);
          setDateWiseClicks(dateWiseClicks);
        } else {
          console.error("Invalid response structure for device clicks");
          // Handle invalid response (e.g., set default values)
          setDeviceClicks({ desktop: 0, mobile: 0, tablet: 0 });
        }

        setIsLoading(false);
      } catch (error) {
        console.error("Error fetching click stats:", error);
        setIsLoading(false);
      }
    };

    fetchClickStats();
  }, []);

  return (
    <div className="dashpart-container">
      <div className="click-stats-card">
        <h2>Total Clicks</h2>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <p className="click-count">{totalClicks}</p>
        )}
      </div>

      <div className="device-stats-card">
        <h2>Device-Wise Clicks</h2>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <ul>
            <li>Desktop: {deviceClicks?.desktop ?? 0}</li>
            <li>Mobile: {deviceClicks?.mobile ?? 0}</li>
            <li>Tablet: {deviceClicks?.tablet ?? 0}</li>
          </ul>
        )}
      </div>

      <div className="date-stats-card">
        <h2>Date-Wise Clicks</h2>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Clicks</th>
              </tr>
            </thead>
            <tbody>
              {dateWiseClicks.map((entry, index) => (
                <tr key={index}>
                  <td>{entry.date}</td>
                  <td>{entry.clicks}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Dashpart;
