import React, { useEffect, useState } from 'react';
import './Analysis.css';
import axios from 'axios';

function Analysis() {
  const [links, setLinks] = useState([]);

  // Function to truncate the original URL if it exceeds 20 words
  const truncateUrl = (url) => {
    const words = url.split(' ');
    if (words.length > 20) {
      return words.slice(0, 20).join(' ') + '...'; // Limit to 20 words and append '...'
    }
    return url; // Return original URL if it's 20 words or less
  };

  // Fetch data when the component mounts
  useEffect(() => {
    const fetchLinks = async () => {
      try {
        const response = await axios.get('http://localhost:4000/anal/get-links', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('authToken')}`, // Send the token
          },
        });

        setLinks(response.data.links); // Store the links data
      } catch (error) {
        console.error('Error fetching links data:', error);
      }
    };

    fetchLinks();
  }, []);

  return (
    <div className="analysis-container">
      <div className="analysis-table-header">
        <ul className="analysis-table">
          <li>
            Timestamp
            <button className="date-sort">
              <img src="sort.png" alt="Sort" />
            </button>
          </li>
          <li>Original Link</li>
          <li>Short Link</li>
          <li>IP Address</li>
          <li>User Device</li>
        </ul>
      </div>

      <div className="analysis-table-body">
        {links.map((link, index) => (
          <ul className="analysis-row" key={index}>
            <li>{new Date(link.createdAt).toLocaleString()}</li>
            <li>
              <a href={link.destinationUrl} target="_blank" rel="noopener noreferrer">
                {truncateUrl(link.destinationUrl)} {/* Truncate the URL if needed */}
              </a>
            </li>
            <li>
              <a href={link.shortUrl} target="_blank" rel="noopener noreferrer">
                {link.shortUrl}
              </a>
            </li>
            <li>{link.ipAddress}</li>
            <li className="analysis-device">{link.device}</li>
          </ul>
        ))}
      </div>
    </div>
  );
}

export default Analysis;
