import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Data.css';

function Data() {
  const [links, setLinks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedLink, setSelectedLink] = useState(null);

  useEffect(() => {
    const fetchLinks = async () => {
      const token = localStorage.getItem('authToken');
      if (!token) {
        console.error('User is not authenticated');
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/get-links', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setLinks(response.data.links);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();
  }, []);

  const getDeviceType = () => {
    if (window.innerWidth <= 768) return 'mobile';
    if (window.innerWidth <= 1024) return 'tablet';
    return 'desktop';
  };

  const handleLinkClick = async (linkId) => {
    const deviceType = getDeviceType();

    try {
      await axios.post('http://localhost:4000/api/track-click', {
        linkId,
        deviceType,
      });
    } catch (error) {
      console.error('Error tracking click:', error);
    }
  };

  const getShortenedLink = (url) => {
    return url.length > 10 ? url.slice(0, 10) + '...' : url;
  };

  const copyToClipboard = (shortUrl) => {
    navigator.clipboard.writeText(shortUrl)
      .then(() => {
        alert('Short link copied to clipboard!');
      })
      .catch((error) => {
        console.error('Error copying to clipboard', error);
      });
  };

  const openModal = (link) => {
    setSelectedLink(link);
  };

  const closeModal = () => {
    setSelectedLink(null);
  };

  const getStatus = (expirationDate) => {
    const now = new Date();
    const expiry = new Date(expirationDate);
    return now <= expiry ? 'Active' : 'Inactive';
  };

  return (
    <div className="data-container">
      <div className="table-header">
        <ul className="data-table">
          <li>Date</li>
          <li>Original Link</li>
          <li>Short Link</li>
          <li>Remarks</li>
          <li>Clicks</li>
          <li>Status</li>
          <li>Action</li>
        </ul>
      </div>

      <div className="table-body">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          links.map((link, index) => (
            <ul className="data-row" key={index}>
              <li>{new Date(link.createdAt).toLocaleString()}</li>
              <li>
                <a
                  href={link.destinationUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  onClick={() => handleLinkClick(link._id)}
                >
                  {getShortenedLink(link.destinationUrl)}
                </a>
              </li>
              <li>
                <a
                  href={link.shortUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  onClick={() => handleLinkClick(link._id)}
                >
                  {getShortenedLink(link.shortUrl)}
                </a>
                <button className="copy" onClick={() => copyToClipboard(link.shortUrl)}>
                  <img src="copy.png" alt="Copy" />
                </button>
              </li>
              <li>{link.remarks}</li>
              <li>{link.clicks || 0}</li>
              <li className={`status-${getStatus(link.expirationDate).toLowerCase()}`}>
                {getStatus(link.expirationDate)}
              </li>
              <li className="action-icons">
                <button onClick={() => openModal(link)}>
                  <img src="edit.png" alt="Edit" />
                </button>
                <button>
                  <img src="delete.png" alt="Delete" />
                </button>
              </li>
            </ul>
          ))
        )}
      </div>

      {selectedLink && (
        <div className="modal">
          <div className="modal-content">
            <h3>Link Details</h3>
            <p>
              <strong>Created At:</strong> {new Date(selectedLink.createdAt).toLocaleString()}
            </p>
            <p>
              <strong>Destination URL:</strong> {selectedLink.destinationUrl}
            </p>
            <p>
              <strong>Short URL:</strong> {selectedLink.shortUrl}
            </p>
            <p>
              <strong>Remarks:</strong> {selectedLink.remarks}
            </p>
            <p>
              <strong>Status:</strong> {getStatus(selectedLink.expirationDate)}
            </p>
            <button onClick={closeModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Data;
