import React, { useState } from "react";
import "./Navbar.css";
import CreateModal from "./Modals/CreateModal"; // Import your modal component

const Navbar = () => {
  const [isModalOpen, setModalOpen] = useState(false); // Modal visibility state

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return ".üåÑGood morning";
    else if (hour < 18) return "‚òÄÔ∏èGood afternoon";
    else return "üåôGood evening";
  };

  const getFormattedDate = () => {
    const today = new Date();
    const options = { weekday: "short", month: "short", day: "numeric" };
    return today.toLocaleDateString(undefined, options);
  };

  return (
    <>
      <nav className="navbar">
        <div className="navbar-left">
          <span role="img" aria-label="sun">
            ‚òÄÔ∏è
          </span>
          <span className="greeting">{`${getGreeting()}, Sujith`}</span>
          <div className="day">
            <span className="date">{getFormattedDate()}</span>
          </div>
        </div>
        <div className="navbar-right">
          <button className="create-button" onClick={() => setModalOpen(true)}>
            + Create new
          </button>
          <input
            type="text"
            placeholder="Search by remarks"
            className="search-input"
          />
          <div className="profile-icon">SU</div>
        </div>
      </nav>

      {/* Modal Component */}
      <CreateModal isOpen={isModalOpen} onClose={() => setModalOpen(false)} />
    </>
  );
};

export default Navbar;
