import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CreateModal from "./Modals/CreateModal";
import Dashpart from "./Modals/Dashpart"; // Import Dashpart component
import jwt_decode from "jwt-decode"; // Corrected the import statement
import "./Dashboard.css";
import './Menu.css';
import './Navbar.css';
import './Phone.css';

const Dashboard = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isModalOpen, setModalOpen] = useState(false);
  const [userId, setUserId] = useState(null);
  const [userName, setUserName] = useState("");
  const [showLogout, setShowLogout] = useState(false);
  const [isPhoneMode, setIsPhoneMode] = useState(false);
  const [showDashpart, setShowDashpart] = useState(false); // State to toggle Dashpart
  const navigate = useNavigate();
  
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      const decoded = jwt_decode(token); // Use jwt_decode here
      setUserId(decoded?.userId); // Assuming userId is in the token
    }
  }, []);
  
  useEffect(() => {
    const handleResize = () => {
      setIsPhoneMode(window.innerWidth <= 1080);
    };
    
    handleResize(); // Initial check
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  useEffect(() => {
    const token = localStorage.getItem("authToken");
    const storedName = localStorage.getItem("userName");

    if (!token) {
      navigate("/login"); // Redirect to login if no token is found
    }

    if (storedName) {
      setUserName(storedName);
    }
  }, [navigate]);

  const getGreeting = () => {
    const hour = new Date().getHours();
    return hour < 12
      ? "🌄 Good morning"
      : hour < 18
      ? "☀️ Good afternoon"
      : "🌙 Good evening";
  };

  const getFormattedDate = () => {
    const today = new Date();
    const options = { weekday: "short", month: "short", day: "numeric" };
    return today.toLocaleDateString(undefined, options);
  };

  const capitalizeFirstLetter = (userName) => {
    if (!userName) return "Guest";
    return userName
      .split(" ")
      .map((word) =>
        word.charAt(0).toUpperCase() + word.slice(1).toLowerCase()
      )
      .join(" ");
  };

  const handleProfileClick = () => {
    setShowLogout((prevState) => !prevState);
  };

  const handleLogout = () => {
    localStorage.removeItem("userName");
    setUserName("");
    setShowLogout(false);
    navigate("/"); // Redirect to home or login
  };

  // Handle Dashboard menu item click to toggle Dashpart
  const handleDashboardClick = () => {
    setShowDashpart(true); // Show Dashpart on click
  };

  return (
    <div className="dashboard-container">
      {/* Navbar Section */}
      <div className="rightpart">
        <nav className="navbar">
          <div className="navbar-left">
            <span className="greeting">
              {`${getGreeting()}, ${capitalizeFirstLetter(userName) || "Guest"}`}
            </span>
            <div className="day">
              <span className="date">{getFormattedDate()}</span>
            </div>
          </div>
          <div className="navbar-right">
            <button
              className="create-button"
              onClick={() => setModalOpen(true)}
            >
              + Create new
            </button>
            <input
              type="text"
              placeholder="Search by remarks"
              className="search-input"
            />
            {showLogout && (
              <button className="logout-button" onClick={handleLogout}>
                Logout
              </button>
            )}
            <div className="profile-icon" onClick={handleProfileClick}>
              {userName
                ? userName.split(" ").length > 1
                  ? userName.split(" ")[0].charAt(0).toUpperCase() +
                    userName.split(" ")[1].charAt(0).toUpperCase()
                  : userName.charAt(0).toUpperCase() +
                    userName.charAt(userName.length - 1).toUpperCase()
                : "G"}
            </div>
          </div>
        </nav>
      </div>

      {/* Menu Section */}
      <div className="phone-menu-container">
        <div className={`phone-menu-content ${isMenuOpen ? "open" : "closed"}`}>
          <ul className="phone-menu-items">
            <li className="phone-menu-item">
              <span className="phone-menu-icon">
                <img src="dash-icon.png" alt="Dashboard Icon" />
              </span>
              <span className="phone-menu-text">Dashboard</span>
            </li>
            <li className="phone-menu-item">
              <span className="phone-menu-icon">
                <img src="link-icon.png" alt="Links Icon" />
              </span>
              <span className="phone-menu-text">Links</span>
            </li>
            <li className="phone-menu-item">
              <span className="phone-menu-icon">
                <img src="Anal-icon.png" alt="Analytics Icon" />
              </span>
              <span className="phone-menu-text">Analytics</span>
            </li>
            <li className="phone-menu-item-setting">
              <span className="phone-menu-icon-setting">⚙️</span>
              <span className="phone-menu-text-setting">Settings</span>
            </li>
          </ul>
        </div>
      </div>

      <div className="content-dash">
        <div className={`menu-container ${isPhoneMode ? "hidden" : ""}`}>
          <div className={`menu-content ${isMenuOpen ? "open" : "closed"}`}>
            <div className="logomenu">
              <img src="menu-logo.png" alt="Logo" />
            </div>
            <ul className={`menu-items ${isPhoneMode ? "show" : ""}`}>
              <li className="menu-item" onClick={handleDashboardClick}>
                <span className="menu-icon">
                  <img src="dash-icon.png" alt="Dashboard Icon" />
                </span>
                <span className="menu-text">Dashboard</span>
              </li>
              <li className="menu-item">
                <span className="menu-icon">
                  <img src="link-icon.png" alt="Links Icon" />
                </span>
                <span className="menu-text">Links</span>
              </li>
              <li className="menu-item">
                <span className="menu-icon">
                  <img src="Anal-icon.png" alt="Analytics Icon" />
                </span>
                <span className="menu-text">Analytics</span>
              </li>
              <li className="menu-item-setting">
                <span className="menu-icon-setting">⚙️</span>
                <span className="menu-text-setting">Settings</span>
              </li>
            </ul>
          </div>
        </div>
        <div>{showDashpart && <Dashpart />}</div>
      </div>

      {/* Show Modal Component */}
      <CreateModal isOpen={isModalOpen} onClose={() => setModalOpen(false)} userId={userId} />
    </div>
  );
};

export default Dashboard;
