import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashpart.css";

function Dashpart() {
  const [totalClicks, setTotalClicks] = useState(0);
  const [deviceClicks, setDeviceClicks] = useState({
    desktop: 0,
    mobile: 0,
    tablet: 0,
  });
  const [dateWiseClicks, setDateWiseClicks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClickStats = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("User is not authenticated.");
        return;
      }

      try {
        const response = await axios.get("http://localhost:4000/api/dash-clicks", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const { totalClicks, deviceClicks, dateWiseClicks } = response.data;

        setTotalClicks(totalClicks || 0);
        setDeviceClicks(deviceClicks || { desktop: 0, mobile: 0, tablet: 0 });

        // Group dateWiseClicks by date to avoid repetition
        const groupedDateWiseClicks = [];
        dateWiseClicks.forEach((entry) => {
          const existing = groupedDateWiseClicks.find(
            (d) => new Date(d.date).toLocaleDateString() === new Date(entry.date).toLocaleDateString()
          );
          if (existing) {
            existing.clicks += entry.clicks;
          } else {
            groupedDateWiseClicks.push(entry);
          }
        });

        setDateWiseClicks(groupedDateWiseClicks);
      } catch (error) {
        console.error("Error fetching click stats:", error);
        setError("Failed to fetch click statistics. Please try again.");
      } finally {
        setIsLoading(false);
      }
    };

    fetchClickStats();
  }, []);

  return (
    <div className="dashpart-container">
      <h1 className="total-clicks">
        Total Clicks: <span>{isLoading ? "Loading..." : totalClicks}</span>
      </h1>

      {error && <p className="error-message">{error}</p>}

      <div className="dashpart-stats">
        <div className="stats-card">
          <h3>Date-wise Clicks</h3>
          {isLoading ? (
            <p>Loading...</p>
          ) : (
            <ul className="date-clicks-list">
              {dateWiseClicks.length > 0 ? (
                dateWiseClicks.map((entry, index) => (
                  <li key={index} className="date-clicks-item">
                    <span>{new Date(entry.date).toLocaleDateString()}</span>
                    <span>{entry.clicks}</span>
                  </li>
                ))
              ) : (
                <p>No data available.</p>
              )}
            </ul>
          )}
        </div>

        <div className="stats-card">
          <h3>Click Devices</h3>
          {isLoading ? (
            <p>Loading...</p>
          ) : (
            <ul className="device-clicks-list">
              <li>Mobile: {deviceClicks.mobile}</li>
              <li>Desktop: {deviceClicks.desktop}</li>
              <li>Tablet: {deviceClicks.tablet}</li>
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashpart;
