import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashpart = () => {
  const [totalClicks, setTotalClicks] = useState(0);
  const [deviceClicks, setDeviceClicks] = useState({
    desktop: 0,
    mobile: 0,
    tablet: 0,
  });
  const [dateWiseClicks, setDateWiseClicks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const fetchClickStats = async () => {
    const token = localStorage.getItem("authToken");
    if (!token) {
      console.error("User is not authenticated");
      return;
    }

    try {
      const response = await axios.get("http://localhost:4000/api/dash-clicks", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      const { totalClicks = 0, deviceClicks = {}, dateWiseClicks = [] } = response.data;

      setTotalClicks(totalClicks);
      setDeviceClicks({
        desktop: deviceClicks.desktop || 0,
        mobile: deviceClicks.mobile || 0,
        tablet: deviceClicks.tablet || 0,
      });
      setDateWiseClicks(dateWiseClicks);
      setIsLoading(false);
    } catch (error) {
      console.error("Error fetching click stats:", error);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchClickStats();
  }, []);

  return (
    <div className="dashboard">
      <h2>Click Statistics</h2>
      <div className="stats-card">
        <h3>Total Clicks</h3>
        <p>{isLoading ? "Loading..." : totalClicks}</p>
      </div>
      <div className="stats-card">
        <h3>Click Devices</h3>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <ul className="device-clicks-list">
            <li>Mobile: {deviceClicks.mobile}</li>
            <li>Desktop: {deviceClicks.desktop}</li>
            <li>Tablet: {deviceClicks.tablet}</li>
          </ul>
        )}
      </div>
      <div className="stats-card">
        <h3>Clicks by Date</h3>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <ul className="date-wise-clicks-list">
            {dateWiseClicks.map((entry, index) => (
              <li key={index}>
                {entry.date}: {entry.clicks}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Dashpart;
