import React, { useState, useEffect } from 'react';
import axios from 'axios';
import EditModal from './EditModal';
import DeleteModal from './DeleteModal';

const Data = () => {
  const [links, setLinks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [linksPerPage] = useState(10);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedLink, setSelectedLink] = useState(null);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [linkToDelete, setLinkToDelete] = useState(null);

  useEffect(() => {
    const fetchLinks = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const response = await axios.get('http://localhost:4000/api/get-links', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setLinks(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching links:', error);
        setIsLoading(false);
      }
    };

    fetchLinks();
  }, []);

  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  const openModal = (link) => setSelectedLink(link);
  const closeModal = () => setSelectedLink(null);

  const openDeleteModal = (link) => {
    setLinkToDelete(link);
    setIsDeleteModalOpen(true);
  };

  const closeDeleteModal = () => {
    setIsDeleteModalOpen(false);
    setLinkToDelete(null);
  };

  const handleDeleteConfirm = async () => {
    try {
      const token = localStorage.getItem('authToken');
      await axios.delete(`http://localhost:4000/api/delete-link/${linkToDelete._id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      // Update the links state after deletion
      setLinks((prevLinks) =>
        prevLinks.filter((link) => link._id !== linkToDelete._id)
      );

      closeDeleteModal();
      alert('Link deleted successfully');
    } catch (error) {
      console.error('Error deleting link:', error);
      alert('Failed to delete the link. Please try again.');
    }
  };

  const handleLinkUpdate = (updatedLink) => {
    setLinks((prevLinks) =>
      prevLinks.map((link) => (link._id === updatedLink._id ? updatedLink : link))
    );
  };

  const copyToClipboard = (text, index) => {
    navigator.clipboard.writeText(text);
    alert(`Copied to clipboard: ${text}`);
  };

  const getShortenedLink = (url) => (url.length > 30 ? `${url.substring(0, 30)}...` : url);

  const getLinkStatus = (expiryDate) => {
    const now = new Date();
    return new Date(expiryDate) > now ? 'Active' : 'Expired';
  };

  // Pagination Logic
  const indexOfLastLink = currentPage * linksPerPage;
  const indexOfFirstLink = indexOfLastLink - linksPerPage;
  const currentLinks = links.slice(indexOfFirstLink, indexOfLastLink);
  const pageNumbers = Array.from({ length: Math.ceil(links.length / linksPerPage) }, (_, i) => i + 1);

  return (
    <div className="data-container">
      {/* Table Header */}
      <div className="table-header">
        <ul className="data-table">
          <li>Date</li>
          <li>Original Link</li>
          <li>Short Link</li>
          <li>Remarks</li>
          <li>Clicks</li>
          <li>Status</li>
          <li>Action</li>
        </ul>
      </div>

      {/* Table Rows */}
      <div className="table-body">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          currentLinks.map((link, index) => (
            <ul className="data-row" key={index}>
              <li>{new Date(link.createdAt).toLocaleString()}</li>
              <li>
                <a href={link.destinationUrl} target="_blank" rel="noopener noreferrer">
                  {getShortenedLink(link.destinationUrl)}
                </a>
              </li>
              <li>
                <a
                  href={link.shortUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  {getShortenedLink(link.shortUrl)}
                </a>
                <button className="copy" onClick={() => copyToClipboard(link.shortUrl, index)}>
                  <img src="copy.png" alt="Copy" />
                </button>
              </li>
              <li>{link.remarks}</li>
              <li className="count">{link.clicks.total}</li>
              <li className="status">{getLinkStatus(link.expiryDate)}</li>
              <li className="action-icons">
                <button className="edit" onClick={() => openModal(link)}>
                  <img src="edit.png" alt="Edit" />
                </button>
                <button className="delete" onClick={() => openDeleteModal(link)}>
                  <img src="delete.png" alt="Delete" />
                </button>
              </li>
            </ul>
          ))
        )}
      </div>

      {/* Pagination Controls */}
      <div className="pagination">
        {pageNumbers.map((number) => (
          <button
            key={number}
            onClick={() => handlePageChange(number)}
            className={currentPage === number ? 'active' : ''}
          >
            {number}
          </button>
        ))}
      </div>

      {/* Edit Modal */}
      <EditModal
        isOpen={selectedLink !== null}
        onClose={closeModal}
        link={selectedLink}
        onUpdate={handleLinkUpdate}
      />

      {/* Delete Modal */}
      {isDeleteModalOpen && (
        <DeleteModal
          onClose={closeDeleteModal}
          onConfirm={handleDeleteConfirm}
        />
      )}
    </div>
  );
};

export default Data;
