import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Setting.css';

const Setting = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchUserData = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:4000/api/user');
        setFormData({
          name: response.data.name,
          email: response.data.email,
          phone: response.data.phone,
        });
      } catch (error) {
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchUserData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = async () => {
    if (!formData.name || !formData.email || !formData.phone) {
      alert('All fields are required.');
      return;
    }

    try {
      const response = await axios.put('http://localhost:4000/api/user', formData);
      console.log('Response from server:', response.data);
      alert('Changes saved successfully!');
    } catch (error) {
      console.error('Error saving changes:', error.response?.data || error.message);
      alert('Failed to save changes.');
    }
  };
  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete your account?')) {
      try {
        await axios.delete('http://localhost:4000/api/user');
        alert('Account deleted successfully.');
        // Redirect user or clean up UI
      } catch (error) {
        console.error('Error deleting account:', error.response?.data || error.message);
        alert('Failed to delete account.');
      }
    }
  };
  

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="setting-container">
      <div className="setting-form" onSubmit={(e) => e.preventDefault()}>
        <div className="setting-field">
          <label className="setting-label">Name</label>
          <input
            className="setting-input"
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div className="setting-field">
          <label className="setting-label">Email id</label>
          <input
            className="setting-input"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
        <div className="setting-field">
          <label className="setting-label">Mobile no.</label>
          <input
            className="setting-input"
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
          />
        </div>
        <button type="button" className="save-button" onClick={handleSave}>
          Save Changes
        </button>
      </div>
      <div className="delete-button-part">
        <button type="button" className="delete-button">
          Delete Account
        </button>
      </div>
    </div>
  );
};

export default Setting;
