import React, { useState } from "react";
import "./CreateModal.css";

const CreateModal = ({ isOpen, onClose, userId }) => { // Added userId to send with the request
  const [url, setUrl] = useState("");
  const [remarks, setRemarks] = useState("");
  const [isExpirationEnabled, setExpirationEnabled] = useState(false);
  const [expirationDate, setExpirationDate] = useState("");

  if (!isOpen) return null;

  const handleClear = () => {
    setUrl("");
    setRemarks("");
    setExpirationEnabled(false);
    setExpirationDate("");
  };

  const handleSubmit = async () => {
    // Prepare the link data
    const data = {
      destinationUrl: url,
      remarks: remarks,
      expirationDate: isExpirationEnabled ? expirationDate : null, // Only send expiration date if enabled
    };

    try {
      const response = await fetch(`/api/user/${userId}/add-link`, {  // Assuming a route to add a link to a specific user
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      const responseData = await response.json();
      if (response.ok) {
        console.log("Link created successfully!");
        handleClear();
        onClose();
      } else {
        console.log("Failed to create link:", responseData.message);
      }
    } catch (error) {
      console.log("Error:", error);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <div className="modal-header">
          <h3>New Link</h3>
          <button className="close-button" onClick={onClose}>âœ•</button>
        </div>
        <div className="modal-body">
          <label>
            Destination Url <span className="required">*</span>
          </label>
          <input
            type="url"
            placeholder="https://web.whatsapp.com/"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />

          <label>
            Remarks <span className="required">*</span>
          </label>
          <textarea
            placeholder="Add remarks"
            value={remarks}
            onChange={(e) => setRemarks(e.target.value)}
          ></textarea>

          <div className="expiration-container">
            <label>Link Expiration</label>
            <div className="expiration-toggle">
              <label className="toggle-switch">
                <input
                  type="checkbox"
                  checked={isExpirationEnabled}
                  onChange={(e) => setExpirationEnabled(e.target.checked)}
                />
                <span className="slider"></span>
              </label>
            </div>
          </div>

          {isExpirationEnabled && (
            <input
              type="datetime-local"
              value={expirationDate}
              onChange={(e) => setExpirationDate(e.target.value)}
            />
          )}
        </div>
        <div className="modal-footer">
          <button className="clear-button" onClick={handleClear}>
            Clear
          </button>
          <button className="create-button" onClick={handleSubmit}>
            Create new
          </button>
        </div>
      </div>
    </div>
  );
};

export default CreateModal;
