import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Data.css';
import EditModal from './EditModal';
import DeleteModal from './DeleteModal';

function Data() {
  const [links, setLinks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedLink, setSelectedLink] = useState(null);
  const [deleteLink, setDeleteLink] = useState(null);
  const [searchQuery, setSearchQuery] = useState(''); // State for search input
  const linksPerPage = 6;

  useEffect(() => {
    const fetchLinks = async () => {
      const token = localStorage.getItem('authToken');

      if (!token) {
        console.error('User is not authenticated');
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/get-links', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setLinks(response.data.links);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();
  }, []);

  const getShortenedLink = (url) => url.slice(0, 10) + '...';

  const copyToClipboard = (shortUrl, index) => {
    navigator.clipboard.writeText(shortUrl)
      .then(() => {
        alert('Short link copied to clipboard!');
        handleLinkClick(shortUrl, index);
      })
      .catch((error) => {
        console.error('Error copying to clipboard', error);
      });
  };

  const handleLinkClick = async (shortUrl, index) => {
    try {
      const encodedShortUrl = encodeURIComponent(shortUrl);
      await axios.get(`http://localhost:4000/api/link-click?shortUrl=${encodedShortUrl}`);
      setLinks((prevLinks) =>
        prevLinks.map((link, i) =>
          i === index ? { ...link, clicks: link.clicks + 1 } : link
        )
      );
    } catch (error) {
      console.error('Error updating click count:', error);
    }
  };

  // Filter links based on search query
  const filteredLinks = links.filter((link) =>
    link.remark.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="data-container">
      <input
        type="text"
        placeholder="Search by remarks..."
        className="search-input"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div className="links-list">
          {filteredLinks.map((link, index) => (
            <div key={link._id} className="link-item">
              <p className="link-url" onClick={() => copyToClipboard(link.shortUrl, index)}>
                {getShortenedLink(link.shortUrl)}
              </p>
              <p className="link-remark">{link.remark}</p>
              <p className="link-clicks">Clicks: {link.clicks}</p>
              <button onClick={() => setSelectedLink(link)}>Edit</button>
              <button onClick={() => setDeleteLink(link)}>Delete</button>
            </div>
          ))}
        </div>
      )}
      {selectedLink && (
        <EditModal
          link={selectedLink}
          onClose={() => setSelectedLink(null)}
          onUpdate={(updatedLink) =>
            setLinks((prevLinks) =>
              prevLinks.map((link) =>
                link._id === updatedLink._id ? updatedLink : link
              )
            )
          }
        />
      )}
      {deleteLink && (
        <DeleteModal
          link={deleteLink}
          onClose={() => setDeleteLink(null)}
          onDelete={(deletedLink) =>
            setLinks((prevLinks) =>
              prevLinks.filter((link) => link._id !== deletedLink._id)
            )
          }
        />
      )}
    </div>
  );
}

export default Data;
