import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Data.css';

function Data() {
  const [links, setLinks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedLink, setSelectedLink] = useState(null);

  // Fetch data from the backend
  useEffect(() => {
    const fetchLinks = async () => {
      const token = localStorage.getItem('authToken'); 

      if (!token) {
        console.error('User is not authenticated');
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/get-links', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setLinks(response.data.links);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();
  }, []);

  // Function to display only the first 10 characters of the URL
  const getShortenedLink = (url) => {
    return url.slice(0, 10) + '...';
  };

  // Function to handle click and send the click data to the server
  const handleLinkClick = async (shortUrl, deviceType) => {
    const token = localStorage.getItem('authToken');
    const userId = localStorage.getItem('userId'); // Assuming user ID is stored in localStorage

    if (!token || !userId) {
      console.error('User is not authenticated');
      return;
    }

    try {
      await axios.post('http://localhost:4000/api/update-click', {
        userId,
        shortUrl,
        device: deviceType,
      }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      console.log('Click count updated');
    } catch (error) {
      console.error('Error updating click count:', error);
    }
  };

  // Function to open the modal
  const openModal = (link) => {
    setSelectedLink(link);
  };

  // Function to close the modal
  const closeModal = () => {
    setSelectedLink(null);
  };

  return (
    <div className="data-container">
      {/* Table Header */}
      <div className="table-header">
        <ul className="data-table">
          <li>Date </li>
          <li>Original Link</li>
          <li>Short Link</li>
          <li>Remarks</li>
          <li>Clicks</li>
          <li>Status</li>
          <li>Action</li>
        </ul>
      </div>

      {/* Table Rows */}
      <div className="table-body">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          links.map((link, index) => (
            <ul className="data-row" key={index}>
              <li>{new Date(link.createdAt).toLocaleString()}</li>
              <li>
                <a href={link.destinationUrl} target="_blank" rel="noopener noreferrer">
                  {getShortenedLink(link.destinationUrl)}
                </a>
              </li>
              <li>
                <a href={link.shortUrl} target="_blank" rel="noopener noreferrer">
                  {getShortenedLink(link.shortUrl)}
                </a>
                <button
                  className="copy"
                  onClick={() => copyToClipboard(link.shortUrl)}
                >
                  <img src="copy.png" alt="Copy" />
                </button>
              </li>
              <li>{link.remarks}</li>
              <li>
                {link.clicks.total}
                {/* Example button for tracking click by device */}
                <button onClick={() => handleLinkClick(link.shortUrl, 'desktop')}>
                  Click as Desktop
                </button>
              </li>
              <li className="status-active">Active</li>
              <li className="action-icons">
                <button>
                  <img src="edit.png" alt="Edit" />
                </button>
                <button>
                  <img src="delete.png" alt="Delete" />
                </button>
              </li>
            </ul>
          ))
        )}
      </div>

      {/* Modal */}
      {selectedLink && (
        <div className="modal">
          <div className="modal-content">
            <h3>Link Details</h3>
            <p>
              <strong>Created At:</strong> {new Date(selectedLink.createdAt).toLocaleString()}
            </p>
            <p>
              <strong>Destination URL:</strong> {selectedLink.destinationUrl}
            </p>
            <p>
              <strong>Short URL:</strong> {selectedLink.shortUrl}
            </p>
            <p>
              <strong>Remarks:</strong> {selectedLink.remarks}
            </p>
            <button onClick={closeModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Data;
