import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Setting.css';

const Setting = () => {
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    phone: '',
  });
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('authToken');  // Correct key name is 'authToken'
  
    if (!token) {
      console.log('No token found, please log in');
      window.location.href = '/login';  // Redirect to login page if no token found
      return;
    }
  
    axios
      .get('http://localhost:4000/api/user', {
        headers: {
          'Authorization': `Bearer ${token}`,  // Add token as Bearer token in Authorization header
        },
      })
      .then((response) => {
        if (response.data.user) {
          setUserData(response.data.user);
        } else {
          console.log('Error fetching user data');
        }
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
        setError('Failed to fetch user data.');
      });
  }, []);

  const handleSaveChanges = () => {
    const token = localStorage.getItem('authToken');
    
    if (!token) {
      console.log('No token found, cannot save changes');
      return;
    }

    // Send the PUT request to update user data
    axios
      .put('http://localhost:4000/api/user', userData, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
      .then((response) => {
        setSuccessMessage('User data updated successfully');
        setError(''); // Reset error message if the update is successful
      })
      .catch((error) => {
        console.error('Error updating user data:', error);
        setError('Failed to update user data.');
        setSuccessMessage(''); // Reset success message if there's an error
      });
  };

  return (
    <div className="setting-container">
      <form className="setting-form">
        <div className="setting-field">
          <label className="setting-label">Name</label>
          <input
            className="setting-input"
            type="text"
            placeholder="Rahul Singh"
            value={userData.name}
            onChange={(e) => setUserData({ ...userData, name: e.target.value })}
          />
        </div>
        <div className="setting-field">
          <label className="setting-label">Email id</label>
          // Handle form input changes correctly in useState
<input
  className="setting-input"
  type="email"
  placeholder="rahulsingh@gmail.com"
  value={userData.email}
  onChange={(e) => setUserData({ ...userData, email: e.target.value })}
/>

        </div>
        <div className="setting-field">
          <label className="setting-label">Mobile no.</label>
          <input
            className="setting-input"
            type="text"
            placeholder="1234567890"
            value={userData.phone}
            onChange={(e) => setUserData({ ...userData, phone: e.target.value })}
          />
        </div>
        <button 
          type="button" 
          className="save-button" 
          onClick={handleSaveChanges}
        >
          Save Changes
        </button>
      </form>

      {/* Success and error messages */}
      {successMessage && <div className="success-message">{successMessage}</div>}
      {error && <div className="error-message">{error}</div>}

      <div className='delete-button-part'>
        <button type="button" className="delete-button">Delete Account</button>
      </div>
    </div>
  );
};

export default Setting;
