import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // Error message
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Reset error message before submitting

    try {
      const response = await axios.post("http://localhost:4000/login", {
        email,
        password,
      });

      if (response.status === 200) {
        console.log("Login successful:", response.data);

        // Store the authentication token and user name in localStorage
        localStorage.setItem("authToken", response.data.token); // Store token
        localStorage.setItem("userName", response.data.name); // Store user name

        // Redirect to the Dashboard
        navigate("/dashboard");
      }
    } catch (error) {
      console.error("Login error:", error);

      // Display error message
      const errorMessage =
        error.response?.data?.message || "Unable to log in. Please try again.";
      setError(errorMessage);
    }
  };

  return (
    <div className="login-container">
      <div className="login-left">
        <img src="wallpaper.png" alt="Background" className="login-image" />
      </div>
      <div className="login-right">
        <div className="top-button">
          <button
            className="register-button"
            onClick={() => navigate("/signup")}
          >
            SignUp
          </button>
          <button className="login-button">Login</button>
        </div>
        <div className="login-form">
          <h2 className="login-text">Login</h2>

          <form onSubmit={handleLogin}>
            <div className="form-group">
              <input
                type="email"
                id="email"
                placeholder="Email id"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <input
                type="password"
                id="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            {/* Show error message */}
            {error && <p className="error-message">{error}</p>}

            <button type="submit" className="login-btn">
              Login
            </button>
          </form>

          <p>
            Don't have an account?{" "}
            <button className="sig" onClick={() => navigate("/signup")}>
              SignUp
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
