import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Data.css';

function Data() {
  const [links, setLinks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedLink, setSelectedLink] = useState(null); // State for modal
  const [currentPage, setCurrentPage] = useState(1); // State for current page
  const linksPerPage = 7; // Number of links per page (changed from 9 to 7)

  // Fetch data from the backend
  useEffect(() => {
    const fetchLinks = async () => {
      const token = localStorage.getItem('authToken'); // Assuming you store the JWT token in localStorage

      if (!token) {
        console.error('User is not authenticated');
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/get-links', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setLinks(response.data.links);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();
  }, []);

  // Function to display only the first 10 characters of the URL
  const getShortenedLink = (url) => {
    return url.slice(0, 10) + '...'; // Get first 10 characters and add '...'
  };

  // Function to copy short link to clipboard
  const copyToClipboard = (shortUrl) => {
    navigator.clipboard.writeText(shortUrl)
      .then(() => {
        alert('Short link copied to clipboard!');
      })
      .catch((error) => {
        console.error('Error copying to clipboard', error);
      });
  };

  // Function to increment click count
  const handleLinkClick = async (shortUrl, originalUrl, index) => {
    try {
      // Call backend to increment the click count for the short URL
      const encodedShortUrl = encodeURIComponent(shortUrl);
      const response = await axios.get(`http://localhost:4000/api/increment-clicks/${encodedShortUrl}`);
  
      // Update the click count on the frontend dynamically
      const updatedLinks = [...links];
      updatedLinks[index].clicks.total = response.data.totalClicks; // Update the click count from the response
      setLinks(updatedLinks); // Update the state with new link data
  
      // Redirect to the original URL after updating click count
      window.location.href = originalUrl;
  
    } catch (error) {
      console.error('Error incrementing click count:', error);
    }
  };
  

  // Function to check link expiry status
  const getLinkStatus = (expiryDate) => {
    const currentDate = new Date();
    const expiry = new Date(expiryDate);
    return expiry < currentDate ? 'Expired' : 'Active'; // If expired, return 'Expired'
  };

  // Handle page change
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Paginate links based on the current page
  const indexOfLastLink = currentPage * linksPerPage;
  const indexOfFirstLink = indexOfLastLink - linksPerPage;
  const currentLinks = links.slice(indexOfFirstLink, indexOfLastLink);

  // Function to open the modal
  const openModal = (link) => {
    setSelectedLink(link);
  };

  // Function to close the modal
  const closeModal = () => {
    setSelectedLink(null);
  };

  // Pagination logic
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(links.length / linksPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <div className="data-container">
      {/* Table Header */}
      <div className="table-header">
        <ul className="data-table">
          <li>Date</li>
          <li>Original Link</li>
          <li>Short Link</li>
          <li>Remarks</li>
          <li>Clicks</li>
          <li>Status</li>
          <li>Action</li>
        </ul>
      </div>

      {/* Table Rows */}
      <div className="table-body">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          currentLinks.map((link, index) => (
            <ul className="data-row" key={index}>
              <li>{new Date(link.createdAt).toLocaleString()}</li>
              <li>
                <a href={link.destinationUrl} target="_blank" rel="noopener noreferrer">
                  {getShortenedLink(link.destinationUrl)}
                </a>
              </li>
              <li>
                <a
                  href={link.shortUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  onClick={() => handleLinkClick(link.shortUrl, index)}
                >
                  {getShortenedLink(link.shortUrl)}
                </a>
                <button className="copy" onClick={() => copyToClipboard(link.shortUrl)}>
                  <img src="copy.png" alt="Copy" />
                </button>
              </li>
              <li>{link.remarks}</li>
              <li className="count">{link.clicks.total}</li> {/* Dynamic click count */}
              <li className={getLinkStatus(link.expiryDate) === 'Expired' ? 'status-expired' : 'status-active'}>
                {getLinkStatus(link.expiryDate)} {/* Dynamic status */}
              </li>
              <li className="action-icons">
                <button>
                  <img src="edit.png" alt="Edit" />
                </button>
                <button>
                  <img src="delete.png" alt="Delete" />
                </button>
              </li>
            </ul>
          ))
        )}
      </div>

      {/* Pagination Controls */}
      <div className="pagination">
        {pageNumbers.map(number => (
          <button
            key={number}
            onClick={() => handlePageChange(number)}
            className={currentPage === number ? 'active' : ''}
          >
            {number}
          </button>
        ))}
      </div>

      {/* Modal */}
      {selectedLink && (
        <div className="modal">
          <div className="modal-content">
            <h3>Link Details</h3>
            <p>
              <strong>Created At:</strong> {new Date(selectedLink.createdAt).toLocaleString()}
            </p>
            <p>
              <strong>Destination URL:</strong> {selectedLink.destinationUrl}
            </p>
            <p>
              <strong>Short URL:</strong> {selectedLink.shortUrl}
            </p>
            <p>
              <strong>Remarks:</strong> {selectedLink.remarks}
            </p>
            <button onClick={closeModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Data;
