import React, { useState, useEffect } from "react";
import "./CreateModal.css";

const CreateModal = ({ isOpen, onClose }) => {
  const [url, setUrl] = useState("");
  const [remarks, setRemarks] = useState("");
  const [isExpirationEnabled, setExpirationEnabled] = useState(false);
  const [expirationDate, setExpirationDate] = useState("");
  const [storedName, setStoredName] = useState(""); // Add state for stored name

  useEffect(() => {
    // Getting token and userName from localStorage on component mount
    const token = localStorage.getItem("authToken"); // Correct token key
    const storedName = localStorage.getItem("userName"); // Getting user name from localStorage
    const storedUserId = localStorage.getItem("userId"); // Getting user ID from localStorage

    // Set name and userId if they exist
    if (token && storedName && storedUserId) {
      setStoredName(storedName); // Set name if available
    } else {
      alert("Session expired. Please log in again.");
      // Optionally, redirect the user to the login page here
      window.location.reload();
    }
  }, []); // Only run once when the component mounts

  if (!isOpen) return null;

  const handleClear = () => {
    setUrl("");
    setRemarks("");
    setExpirationEnabled(false);
    setExpirationDate("");
  };

  const handleSubmit = async () => {
    const token = localStorage.getItem("authToken"); // Correct token key
    const userId = localStorage.getItem("userId"); // Ensure this key is 'userId'

    console.log("Token:", token); // Debugging token
    console.log("User ID:", userId); // Debugging userId

    // Check if token and userId are available
    if (!token || !userId) {
      alert("Session expired. Please log in again.");
      localStorage.removeItem("authToken"); // Clear the token if expired
      localStorage.removeItem("userId"); // Clear the userId if expired
      return;
    }

    // Validate the form inputs
    if (!url.trim() || !remarks.trim()) {
      alert("Please fill out all required fields.");
      return;
    }

    if (isExpirationEnabled && !expirationDate) {
      alert("Please select an expiration date.");
      return;
    }

    // Prepare the payload for the API request
    const payload = {
      destinationUrl: url,
      remarks,
      expirationDate: isExpirationEnabled ? expirationDate : null,
    };

    try {
      // API request to add a new link
      const response = await fetch(`http://localhost:4000/users/${userId}/links`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`, // Sending the token as authorization header
        },
        body: JSON.stringify(payload),
      });

      const data = await response.json();
      if (response.ok) {
        console.log("Link added:", data);
        handleClear(); // Clear the form after successful submission
        onClose(); // Close the modal
      } else if (response.status === 401) {
        alert("Session expired. Please log in again.");
        localStorage.removeItem("authToken"); // Remove expired token
        localStorage.removeItem("userId"); // Remove expired userId
        window.location.reload(); // Optionally redirect to login page
      } else {
        alert(data.message || "An error occurred. Please try again.");
      }
    } catch (error) {
      console.error("Network error:", error);
      alert("Network issue. Please check your connection.");
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <div className="modal-header">
          <h3>New Link</h3>
          <button className="close-button" onClick={onClose}>
            ✕
          </button>
        </div>
        <div className="modal-body">
          <label>
            Destination URL <span className="required">*</span>
          </label>
          <input
            type="url"
            placeholder="https://example.com"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
          <label>
            Remarks <span className="required">*</span>
          </label>
          <textarea
            placeholder="Add remarks"
            value={remarks}
            onChange={(e) => setRemarks(e.target.value)}
          ></textarea>

          <div className="expiration-container">
            <label>Link Expiration</label>
            <label className="toggle-switch">
              <input
                type="checkbox"
                checked={isExpirationEnabled}
                onChange={(e) => setExpirationEnabled(e.target.checked)}
              />
              <span className="slider"></span>
            </label>
          </div>

          {isExpirationEnabled && (
            <input
              type="datetime-local"
              value={expirationDate}
              onChange={(e) => setExpirationDate(e.target.value)}
            />
          )}
        </div>
        <div className="modal-footer">
          <button className="clear-button" onClick={handleClear}>
            Clear
          </button>
          <button className="create-button" onClick={handleSubmit}>
            Create New
          </button>
        </div>
      </div>
    </div>
  );
};

export default CreateModal;
