import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // Error message
  const navigate = useNavigate();

  const handleLogin = async (email, password) => {
    const response = await fetch('http://localhost:4000/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
    });

    const data = await response.json();
    if (response.ok) {
        localStorage.setItem('userName', data.user.name);  // Store username
        localStorage.setItem('token', data.token);  // Store token
        setUserName(data.user.name);  // Set username in React state
    } else {
        console.log(data.message);  // Handle login error
    }
};


  return (
    <div className="login-container">
      <div className="login-left">
        <img src="wallpaper.png" alt="Background" className="login-image" />
      </div>
      <div className="login-right">
        <div className="top-button">
          <button
            className="register-button"
            onClick={() => navigate("/signup")}
          >
            SignUp
          </button>
          <button className="login-button">Login</button>
        </div>
        <div className="login-form">
          <h2 className="login-text">Login</h2>

          <form onSubmit={handleLogin}>
            <div className="form-group">
              <input
                type="email"
                id="email"
                placeholder="Email id"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <input
                type="password"
                id="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            {/* Show error message */}
            {error && <p className="error-message">{error}</p>}

            <button type="submit" className="login-btn">
              Login
            </button>
          </form>

          <p>
            Don't have an account?{" "}
            <button className="sig" onClick={() => navigate("/signup")}>
              SignUp
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
