import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Data.css';
import EditModal from './EditModal';
import DeleteModal from "./DeleteModal";

function Data({ searchQuery, setSearchQuery }) {
  const [links, setLinks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedLink, setSelectedLink] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [deleteLink, setDeleteLink] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc');
  
  const linksPerPage = 6;

  // Filter the links based on the search query
  const filteredLinks = links.filter((link) =>
    link.remarks.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Fetch data from the backend
  useEffect(() => {
    const fetchLinks = async () => {
      const token = localStorage.getItem('authToken');
      if (!token) {
        console.error('User is not authenticated');
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/get-links', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setLinks(response.data.links);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();
  }, []);

  // Sort links by date
  const sortByDate = () => {
    const sortedLinks = [...filteredLinks].sort((a, b) => {
      const dateA = new Date(a.createdAt);
      const dateB = new Date(b.createdAt);
      return sortOrder === 'asc' ? dateA - dateB : dateB - dateA;
    });
    setLinks(sortedLinks);
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  // Directly calculate status here based on the expiration date
  const getLinkStatus = (expiryDate) => {
    if (!expiryDate) {
      return 'Inactive'; // If there's no expiry date, consider it inactive
    }

    const currentDate = new Date();
    const expiry = new Date(expiryDate);

    if (expiry < currentDate) return 'Inactive';

    const timeDifference = expiry - currentDate;
    const oneDayInMillis = 24 * 60 * 60 * 1000;

    if (timeDifference <= oneDayInMillis) return 'Expiring Soon';

    return 'Active'; // If the link is not expired, it's active
  };

  // Handle page change
  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  // Pagination
  const indexOfLastLink = currentPage * linksPerPage;
  const indexOfFirstLink = indexOfLastLink - linksPerPage;
  const currentLinks = filteredLinks.slice(indexOfFirstLink, indexOfLastLink);

  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(filteredLinks.length / linksPerPage); i++) pageNumbers.push(i);

  return (
    <div className="data-container">
      <div className="table-header">
        <ul className="data-table">
          <li>
            Date
            <button className="date-sort" onClick={sortByDate}>
              <img src="sort.png" alt="Sort" />
            </button>
          </li>
          <li>Original Link</li>
          <li>Short Link</li>
          <li>Remarks</li>
          <li>Clicks</li>
          <li>Status</li> {/* Updated this section */}
          <li>Action</li>
        </ul>
      </div>

      <div className="table-body">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          currentLinks.map((link, index) => (
            <ul className="data-row" key={index}>
              <li>{new Date(link.createdAt).toLocaleString()}</li>
              <li>
                <a href={link.destinationUrl} target="_blank" rel="noopener noreferrer">
                  {link.destinationUrl.slice(0, 10)}...
                </a>
              </li>
              <li>
                <a href={link.shortUrl} target="_blank" rel="noopener noreferrer">
                  {link.shortUrl.slice(0, 10)}...
                </a>
              </li>
              <li>{link.remarks}</li>
              <li className="count">{link.clicks.total}</li>
              <li className='status'>{getLinkStatus(link.expirationDate)}</li> {/* Status based on expiration */}
              <li className="action-icons">
                <button className="edit" onClick={() => setSelectedLink(link)}>
                  <img src="edit.png" alt="Edit" />
                </button>
                <button className="delete" onClick={() => setDeleteLink(link)}>
                  <img src="delete.png" alt="Delete" />
                </button>
              </li>
            </ul>
          ))
        )}
      </div>

      <div className="pagination">
        {pageNumbers.map(number => (
          <button
            key={number}
            onClick={() => handlePageChange(number)}
            className={currentPage === number ? 'active' : ''}
          >
            {number}
          </button>
        ))}
      </div>

      <EditModal
        isOpen={selectedLink !== null}
        onClose={() => setSelectedLink(null)}
        link={selectedLink}
        onUpdate={handleLinkUpdate}
      />

      {deleteLink && (
        <DeleteModal
          onClose={() => setDeleteLink(null)}
          onConfirm={handleDeleteConfirm}
        />
      )}
    </div>
  );
}

export default Data;
