import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Setting.css';

const Setting = () => {
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    phone: '',
  });

  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);  // Loading state

  useEffect(() => {
    const fetchUserData = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('authToken');
        const response = await axios.get('http://localhost:4000/api/user', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUserData({
          name: response.data.name,
          email: response.data.email,
          phone: response.data.phone,
        });
      } catch (error) {
        console.error('Error fetching user data:', error);
        setError('Failed to fetch user data. Please try again.');
      } finally {
        setLoading(false);
      }
    };
    fetchUserData();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const saveChanges = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('authToken');
      const response = await axios.put('http://localhost:4000/api/user', userData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      alert('Changes saved successfully');
    } catch (error) {
      console.error('Error saving changes:', error);
      setError('Failed to save changes. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const deleteAccount = async () => {
    if (window.confirm('Are you sure you want to delete your account? This action cannot be undone.')) {
      setLoading(true);
      try {
        const token = localStorage.getItem('authToken');
        const response = await axios.delete('http://localhost:4000/api/user', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        alert('Account deleted successfully');
        // Optionally, redirect to login or logout
      } catch (error) {
        console.error('Error deleting account:', error);
        setError('Failed to delete account. Please try again.');
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="setting-container">
      <form className="setting-form">
        <div className="setting-field">
          <label className="setting-label">Name</label>
          <input
            className="setting-input"
            type="text"
            name="name"
            value={userData.name}
            onChange={handleInputChange}
            placeholder="Rahul Singh"
          />
        </div>
        <div className="setting-field">
          <label className="setting-label">Email id</label>
          <input
            className="setting-input"
            type="email"
            name="email"
            value={userData.email}
            onChange={handleInputChange}
            placeholder="rahulsingh@gmail.com"
          />
        </div>
        <div className="setting-field">
          <label className="setting-label">Mobile no.</label>
          <input
            className="setting-input"
            type="text"
            name="phone"
            value={userData.phone}
            onChange={handleInputChange}
            placeholder="1234567890"
          />
        </div>
        {error && <p className="error-message">{error}</p>}
        <button type="button" className="save-button" onClick={saveChanges} disabled={loading}>
          {loading ? 'Saving...' : 'Save Changes'}
        </button>
      </form>
      <div className="delete-button-part">
        <button type="button" className="delete-button" onClick={deleteAccount} disabled={loading}>
          {loading ? 'Deleting...' : 'Delete Account'}
        </button>
      </div>
    </div>
  );
};

export default Setting;
