import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Dashpart.css';

const Dashpart = () => {
  const [clickData, setClickData] = useState(null);
  const [deviceData, setDeviceData] = useState(null);

  useEffect(() => {
    const fetchClickData = async () => {
      const token = localStorage.getItem('authToken'); // Assuming JWT token is stored in localStorage

      try {
        // Fetch the click stats
        const response = await axios.get('http://localhost:4000/api/click-stats', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setClickData(response.data.dateWiseClicks);
        setDeviceData(response.data.deviceWiseClicks);
      } catch (error) {
        console.error('Error fetching click data:', error);
      }
    };

    fetchClickData();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">
        Total Clicks <span className="text-blue-600">{clickData?.totalClicks || 0}</span>
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
        {/* Date-wise Clicks */}
        <div className="p-4 border rounded-lg shadow-sm">
          <h2 className="text-lg font-semibold mb-4">Date-wise Clicks</h2>
          <div className="space-y-2">
            {clickData?.clicksByDate.map((item) => (
              <div key={item.date} className="flex items-center justify-between">
                <span>{item.date}</span>
                <div
                  className="flex-1 mx-4 h-2 bg-blue-600"
                  style={{ width: `${(item.clicks / clickData.maxClicks) * 100}%` }}
                ></div>
                <span>{item.clicks}</span>
              </div>
            ))}
          </div>
        </div>

        {/* Click Devices */}
        <div className="p-4 border rounded-lg shadow-sm">
          <h2 className="text-lg font-semibold mb-4">Click Devices</h2>
          <div className="space-y-2">
            {deviceData?.map((device) => (
              <div key={device.type} className="flex items-center justify-between">
                <span>{device.type}</span>
                <div
                  className="flex-1 mx-4 h-2 bg-blue-600"
                  style={{ width: `${(device.clicks / deviceData.reduce((a, b) => a + b.clicks, 0)) * 100}%` }}
                ></div>
                <span>{device.clicks}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashpart;
