// Data.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Data.css';
import EditModal from './EditModal';
import DeleteModal from './DeleteModal';

function Data() {
  const [links, setLinks] = useState([]);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [linkToDelete, setLinkToDelete] = useState(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [linkToEdit, setLinkToEdit] = useState(null);

  // Fetch links on component mount
  useEffect(() => {
    const fetchLinks = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const response = await axios.get('http://localhost:4000/api/links', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setLinks(response.data);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();
  }, []);

  // Delete modal handlers
  const openDeleteModal = (link) => {
    setLinkToDelete(link);
    setIsDeleteModalOpen(true);
  };

  const closeDeleteModal = () => {
    setLinkToDelete(null);
    setIsDeleteModalOpen(false);
  };

  const confirmDelete = async () => {
    try {
      const token = localStorage.getItem('authToken');
      await axios.delete(`http://localhost:4000/api/delete-link/${linkToDelete._id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setLinks(links.filter((link) => link._id !== linkToDelete._id));
      closeDeleteModal();
    } catch (error) {
      console.error('Error deleting link:', error);
    }
  };

  // Edit modal handlers
  const openEditModal = (link) => {
    setLinkToEdit(link);
    setIsEditModalOpen(true);
  };

  const closeEditModal = () => {
    setLinkToEdit(null);
    setIsEditModalOpen(false);
  };

  const updateLink = (updatedLink) => {
    setLinks(links.map((link) => (link._id === updatedLink._id ? updatedLink : link)));
  };

  return (
    <div className="data-container">
      <h1>Links</h1>
      <div className="table-body">
        {links.map((link, index) => (
          <ul className="data-row" key={index}>
            <li>{link.title}</li>
            <li>{link.url}</li>
            <li>{link.description}</li>
            <li className="action-icons">
              <button className="edit" onClick={() => openEditModal(link)}>
                <img src="edit.png" alt="Edit" />
              </button>
              <button className="delete" onClick={() => openDeleteModal(link)}>
                <img src="delete.png" alt="Delete" />
              </button>
            </li>
          </ul>
        ))}
      </div>

      {/* Modals */}
      {isEditModalOpen && (
        <EditModal
          link={linkToEdit}
          onClose={closeEditModal}
          onUpdate={updateLink}
        />
      )}

      {isDeleteModalOpen && (
        <DeleteModal onClose={closeDeleteModal} onConfirm={confirmDelete} />
      )}
    </div>
  );
}

export default Data;
