import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Data.css';
import EditModal from './EditModal';
import DeleteModal from './DeleteModal'; // Import the DeleteModal component

function Data() {
  const [links, setLinks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedLink, setSelectedLink] = useState(null); // State for EditModal
  const [deleteLink, setDeleteLink] = useState(null); // State for DeleteModal
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false); // Track DeleteModal visibility
  const [currentPage, setCurrentPage] = useState(1);
  const linksPerPage = 7;

  useEffect(() => {
    const fetchLinks = async () => {
      const token = localStorage.getItem('authToken');
      if (!token) {
        console.error('User is not authenticated');
        return;
      }
      try {
        const response = await axios.get('http://localhost:4000/api/get-links', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setLinks(response.data.links);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };
    fetchLinks();
  }, []);

  const openDeleteModal = (link) => {
    setDeleteLink(link); // Set the link to be deleted
    setIsDeleteModalOpen(true); // Open the modal
  };

  const closeDeleteModal = () => {
    setIsDeleteModalOpen(false); // Close the modal
    setDeleteLink(null); // Clear the link to be deleted
  };

  const confirmDelete = async () => {
    if (!deleteLink) return;

    try {
      await axios.delete(`http://localhost:4000/api/delete-link/${deleteLink._id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('authToken')}`,
        },
      });
      setLinks(links.filter(link => link._id !== deleteLink._id)); // Remove deleted link from the list
      closeDeleteModal(); // Close the modal
    } catch (error) {
      console.error('Error deleting link:', error);
    }
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const indexOfLastLink = currentPage * linksPerPage;
  const indexOfFirstLink = indexOfLastLink - linksPerPage;
  const currentLinks = links.slice(indexOfFirstLink, indexOfLastLink);

  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(links.length / linksPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <div className="data-container">
      <div className="table-header">
        <ul className="data-table">
          <li>Date</li>
          <li>Original Link</li>
          <li>Short Link</li>
          <li>Remarks</li>
          <li>Clicks</li>
          <li>Status</li>
          <li>Action</li>
        </ul>
      </div>

      <div className="table-body">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          currentLinks.map((link, index) => (
            <ul className="data-row" key={index}>
              <li>{new Date(link.createdAt).toLocaleString()}</li>
              <li>
                <a href={link.destinationUrl} target="_blank" rel="noopener noreferrer">
                  {link.destinationUrl.slice(0, 10)}...
                </a>
              </li>
              <li>
                <a
                  href={link.shortUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  {link.shortUrl.slice(0, 10)}...
                </a>
              </li>
              <li>{link.remarks}</li>
              <li className="count">{link.clicks.total}</li>
              <li className="status">Active</li>
              <li className="action-icons">
                <button className="edit" onClick={() => setSelectedLink(link)}>
                  <img src="edit.png" alt="Edit" />
                </button>
                <button className="delete" onClick={() => openDeleteModal(link)}>
                  <img src="delete.png" alt="Delete" />
                </button>
              </li>
            </ul>
          ))
        )}
      </div>

      <div className="pagination">
        {pageNumbers.map(number => (
          <button
            key={number}
            onClick={() => handlePageChange(number)}
            className={currentPage === number ? 'active' : ''}
          >
            {number}
          </button>
        ))}
      </div>

      {/* Modals */}
      <EditModal
        isOpen={selectedLink !== null}
        onClose={() => setSelectedLink(null)}
        link={selectedLink}
        onUpdate={updatedLink => {
          setLinks(links.map(link => link._id === updatedLink._id ? updatedLink : link));
        }}
      />

      <DeleteModal
        onClose={closeDeleteModal}
        onConfirm={confirmDelete}
        isOpen={isDeleteModalOpen}
      />
    </div>
  );
}

export default Data;
