import React, { useState, useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./Data.css";
import EditModal from "./EditModal";
import DeleteModal from "./DeleteModal";

toast.configure();

function Data({ searchQuery, setSearchQuery }) {
  const [links, setLinks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedLink, setSelectedLink] = useState(null);
  const [deleteLink, setDeleteLink] = useState(null);
  const [sortOrder, setSortOrder] = useState("asc");

  useEffect(() => {
    const fetchLinks = async () => {
      try {
        const response = await axios.get("http://localhost:4000/api/get-links", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("authToken")}`,
          },
        });
        setLinks(response.data.links);
        setIsLoading(false);
      } catch (error) {
        toast.error("Error fetching links!", { position: "bottom-left" });
      }
    };

    fetchLinks();
  }, []);

  const copyToClipboard = (shortUrl) => {
    navigator.clipboard
      .writeText(shortUrl)
      .then(() => {
        toast.success("Short link copied to clipboard!", { position: "bottom-left", autoClose: 2000 });
      })
      .catch(() => {
        toast.error("Failed to copy!", { position: "bottom-left" });
      });
  };

  const handleDeleteConfirm = async () => {
    if (!deleteLink) return;
    try {
      await axios.delete(`http://localhost:4000/api/delete-link/${deleteLink._id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("authToken")}` },
      });
      setLinks(links.filter((link) => link._id !== deleteLink._id));
      setDeleteLink(null);
      toast.success("Link deleted successfully!", { position: "bottom-left" });
    } catch (error) {
      toast.error("Error deleting link!", { position: "bottom-left" });
    }
  };

  return (
    <div className="data-container">
      <div className="table-body">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          links.map((link, index) => (
            <ul className="data-row" key={index}>
              <li>
                <a href={link.shortUrl} target="_blank" rel="noopener noreferrer">
                  {link.shortUrl}
                </a>
                <button className="copy" onClick={() => copyToClipboard(link.shortUrl)}>
                  <img src="copy.png" alt="Copy" />
                </button>
              </li>
              <li className="action-icons">
                <button className="delete" onClick={() => setDeleteLink(link)}>
                  <img src="delete.png" alt="Delete" />
                </button>
              </li>
            </ul>
          ))
        )}
      </div>

      {deleteLink && <DeleteModal onClose={() => setDeleteLink(null)} onConfirm={handleDeleteConfirm} />}
    </div>
  );
}

export default Data;
