import React, { useState, useEffect } from 'react';
import './Setting.css';

const Setting = () => {
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    phone: ''
  });

  // Fetch the user details when the component is mounted
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch('/api/user', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            // Add authorization header here if needed (e.g., JWT token)
          },
        });

        if (response.ok) {
          const data = await response.json();
          setUserData({
            name: data.name,
            email: data.email,
            phone: data.phone
          });
        } else {
          console.error('Error fetching user data');
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUserData();
  }, []);

  // Handle form submission for updating user details
  const handleSubmit = async (e) => {
    e.preventDefault();

    const { name, email, phone } = userData;
    
    try {
      const response = await fetch('/api/user', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          // Add authorization header here if needed (e.g., JWT token)
        },
        body: JSON.stringify({ name, email, phone })
      });

      if (response.ok) {
        const updatedUser = await response.json();
        setUserData(updatedUser);  // Update local state with updated data
        alert('User details updated successfully');
      } else {
        console.error('Error updating user data');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Handle input changes to update state
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div className="setting-container">
      <form className="setting-form" onSubmit={handleSubmit}>
        <div className="setting-field">
          <label className="setting-label">Name</label>
          <input
            className="setting-input"
            type="text"
            name="name"
            value={userData.name}
            onChange={handleChange}
            placeholder="Rahul Singh"
          />
        </div>
        <div className="setting-field">
          <label className="setting-label">Email id</label>
          <input
            className="setting-input"
            type="email"
            name="email"
            value={userData.email}
            onChange={handleChange}
            placeholder="rahulsingh@gmail.com"
          />
        </div>
        <div className="setting-field">
          <label className="setting-label">Mobile no.</label>
          <input
            className="setting-input"
            type="text"
            name="phone"
            value={userData.phone}
            onChange={handleChange}
            placeholder="1234567890"
          />
        </div>
        <button type="submit" className="save-button">Save Changes</button>
      </form>
      <div className="delete-button-part">
        <button type="button" className="delete-button">Delete Account</button>
      </div>
    </div>
  );
};

export default Setting;
