import React from "react";
import "./Navbar.css";

import { useState } from "react";

const Navbar = () => {
  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "Good morning";
    else if (hour < 18) return "Good afternoon";
    else return "Good evening";
  };

  const getFormattedDate = () => {
    const today = new Date();
    const options = { weekday: "short", month: "short", day: "numeric" };
    return today.toLocaleDateString(undefined, options);
  };

  const [isModalOpen, setModalOpen] = useState(false);

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <span role="img" aria-label="sun">
          ☀️
        </span>
        <span className="greeting">{`${getGreeting()}, Sujith`}</span>
       <div className="day"> <span className="date">{getFormattedDate()}</span></div>
      </div>
      <div className="navbar-right">
        <button className="create-button" onClick={() => setModalOpen(true)}>+ Create new</button>
        <input
          type="text"
          placeholder="Search by remarks"
          className="search-input"
        />
        <div className="profile-icon">SU</div>
      </div>
    </nav>
  );
};

export default Navbar;
