import React, { useState } from "react";
import Dashpart from "./Dashpart"; // Import your dashboard component
import Data from "./Data"; // Import your data component
import Setting from "./Setting"; // Import your settings component
import "./Dashboard.css"; // Include your CSS file for styling

const Dashboard = () => {
  const [showDashpart, setShowDashpart] = useState(true);
  const [showData, setShowData] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [isPhoneMode, setIsPhoneMode] = useState(false);

  const handleDashboardClick = () => {
    setShowDashpart(true);
    setShowData(false);
    setShowSettings(false);
  };

  const handleDataClick = () => {
    setShowDashpart(false);
    setShowData(true);
    setShowSettings(false);
  };

  const handleSettingsClick = () => {
    setShowDashpart(false);
    setShowData(false);
    setShowSettings(true);
  };

  const toggleMenu = () => {
    setIsPhoneMode(!isPhoneMode);
  };

  return (
    <div className="dashboard">
      {/* Left Menu */}
      <div className={`left-menu ${isPhoneMode ? "phone-mode" : ""}`}>
        <button className="toggle-menu" onClick={toggleMenu}>
          {isPhoneMode ? "Close Menu" : "Open Menu"}
        </button>
        <ul className={`menu-items ${isPhoneMode ? "show" : ""}`}>
          <li className="menu-item" onClick={handleDashboardClick}>
            <span className="menu-icon">
              <img src="dash-icon.png" alt="Dashboard Icon" />
            </span>
            <span className="menu-text">Dashboard</span>
          </li>
          <li className="menu-item" onClick={handleDataClick}>
            <span className="menu-icon">
              <img src="link-icon.png" alt="Links Icon" />
            </span>
            <span className="menu-text">Links</span>
          </li>
          <li className="menu-item">
            <span className="menu-icon">
              <img src="Anal-icon.png" alt="Analytics Icon" />
            </span>
            <span className="menu-text">Analytics</span>
          </li>
          <li className="menu-item-setting" onClick={handleSettingsClick}>
            <span className="menu-icon-setting">⚙️</span>
            <span className="menu-text-setting">Settings</span>
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="mid-content">
        {showDashpart && <Dashpart />}
        {showData && <Data />}
        {showSettings && <Setting />}
      </div>
    </div>
  );
};

export default Dashboard;
