import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashpart.css"; // Optional CSS file for styling

const Dashpart = () => {
  const [dateWiseClicks, setDateWiseClicks] = useState([]);
  const [clickDevices, setClickDevices] = useState([]);
  const [totalClicks, setTotalClicks] = useState(0);
  const [error, setError] = useState(""); // Error state to capture authentication or other issues

  // Fetch click data on component mount
  useEffect(() => {
    const fetchClickData = async () => {
      const token = localStorage.getItem("token");

      // Check if token is available
      if (!token) {
        setError("Authentication token missing. Please log in again.");
        return;
      }

      try {
        // Replace with your actual API endpoint to get clicks data
        const response = await axios.get("http://localhost:4000/api/get-clicks", {
          headers: {
            Authorization: `Bearer ${token}`, // Assuming the JWT token is stored in localStorage
          },
        });

        const data = response.data; // Assuming response data contains the clicks data

        // Set state with fetched data
        setDateWiseClicks(data.dateWiseClicks);
        setClickDevices(data.clickDevices);
        setTotalClicks(data.totalClicks);
      } catch (error) {
        // Handle errors: Unauthorized, network issues, etc.
        if (error.response && error.response.status === 401) {
          setError("Unauthorized access. Please log in again.");
        } else {
          setError("An error occurred while fetching data.");
        }
        console.error("Error fetching click data:", error);
      }
    };

    fetchClickData();
  }, []);

  return (
    <div className="dashpart-container">
      {error && <div className="error-message">{error}</div>} {/* Display error message */}
      <h1>Total Clicks <span className="total-clicks">{totalClicks}</span></h1>
      <div className="dashpart-grid">
        {/* Date-wise Clicks */}
        <div className="dashpart-card">
          <div className="chart-section">
            <h2>Date-wise Clicks</h2>
            <ul>
              {dateWiseClicks.map((data, index) => (
                <li key={index} className="date-row">
                  <span className="date">{data.date}</span>
                  <div className="bar" style={{ width: `${data.clicks / 10}px` }}></div>
                  <span className="clicks">{data.clicks}</span>
                </li>
              ))}
            </ul>
          </div>
        </div>

        {/* Click Devices */}
        <div className="dashpart-card">
          <div className="chart-section">
            <h2>Click Devices</h2>
            <ul>
              {clickDevices.map((data, index) => (
                <li key={index} className="device-row">
                  <span className="device">{data.device}</span>
                  <div className="bar" style={{ width: `${data.clicks * 5}px` }}></div>
                  <span className="clicks">{data.clicks}</span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashpart;
