import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashpart.css";

function Dashpart() {
  const [totalClicks, setTotalClicks] = useState(0);
  const [deviceClicks, setDeviceClicks] = useState({
    desktop: 0,
    mobile: 0,
    tablet: 0,
  });
  const [dateWiseClicks, setDateWiseClicks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClickStats = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("User is not authenticated.");
        return;
      }

      try {
        const response = await axios.get("http://localhost:4000/api/dash-clicks", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const { totalClicks, deviceClicks, dateWiseClicks } = response.data;

        setTotalClicks(totalClicks || 0);
        setDeviceClicks(deviceClicks || { desktop: 0, mobile: 0, tablet: 0 });

        // Date-wise click handling:
        setDateWiseClicks(dateWiseClicks || []);
      } catch (error) {
        console.error("Error fetching click stats:", error);
        setError("Failed to fetch click statistics. Please try again.");
      } finally {
        setIsLoading(false);
      }
    };

    fetchClickStats();
  }, []);

  return (
    <div className="dashpart-container">
      <h1 className="total-clicks">
        Total Clicks: <span>{isLoading ? "Loading..." : totalClicks}</span>
      </h1>

      {error && <p className="error-message">{error}</p>}

      <div className="dashpart-stats">
        <div className="stats-card">
          <h3>Date-wise Clicks</h3>
          {isLoading ? (
            <p>Loading...</p>
          ) : (
            <div className="date-clicks-list">
              {dateWiseClicks.length > 0 ? (
                dateWiseClicks.map((entry, index) => (
                  <div key={index} className="date-clicks-item">
                    <span>{entry.date}</span>
                    <div
                      className="click-bar-container"
                    >
                      <div
                        className="click-bar"
                        style={{
                          width: `${(entry.clicks / totalClicks) * 100}%`, // dynamic bar width based on click count
                        }}
                      ></div>
                      <span className="click-count">{entry.clicks}</span>
                    </div>
                  </div>
                ))
              ) : (
                <p>No data available.</p>
              )}
            </div>
          )}
        </div>

        <div className="stats-card">
          <h3>Click Devices</h3>
          {isLoading ? (
            <p>Loading...</p>
          ) : (
            <ul className="device-clicks-list">
              {Object.entries(deviceClicks).map(([device, clicks]) => (
                <li key={device}>
                  {device.charAt(0).toUpperCase() + device.slice(1)}: {clicks}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashpart;
