import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Data.css';

function Data() {
  const [links, setLinks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const linksPerPage = 7;

  // Fetch data from the backend
  useEffect(() => {
    const fetchLinks = async () => {
      const token = localStorage.getItem('authToken');
      if (!token) {
        console.error('User is not authenticated');
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/get-links', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setLinks(response.data.links);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();
  }, []);

  // Function to display only the first 10 characters of the URL
  const getShortenedLink = (url) => url.slice(0, 10) + '...';

  // Function to handle link expiry status
  const getLinkStatus = (expiryDate) => {
    const currentDate = new Date();
    const expiry = new Date(expiryDate);

    if (expiry < currentDate) return 'Expired';
    const timeDifference = expiry - currentDate;
    const oneDayInMillis = 24 * 60 * 60 * 1000;
    return timeDifference <= oneDayInMillis ? 'Expiring Soon' : 'Active';
  };

  // Handle page change
  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  // Paginate links
  const indexOfLastLink = currentPage * linksPerPage;
  const indexOfFirstLink = indexOfLastLink - linksPerPage;
  const currentLinks = links.slice(indexOfFirstLink, indexOfLastLink);

  return (
    <div className="data-container">
      {/* Table Header */}
      <div className="table-header">
        <ul className="data-table">
          <li>Date</li>
          <li>Original Link</li>
          <li>Short Link</li>
          <li>Remarks</li>
          <li>Clicks</li>
          <li>Status</li>
          <li>Action</li>
        </ul>
      </div>

      {/* Table Rows */}
      <div className="table-body">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          currentLinks.map((link, index) => {
            const status = getLinkStatus(link.expiryDate);
            return (
              <ul className="data-row" key={index}>
                <li>{new Date(link.createdAt).toLocaleString()}</li>
                <li>
                  <a href={link.destinationUrl} target="_blank" rel="noopener noreferrer">
                    {getShortenedLink(link.destinationUrl)}
                  </a>
                </li>
                <li>
                  <a href={link.shortUrl} target="_blank" rel="noopener noreferrer">
                    {getShortenedLink(link.shortUrl)}
                  </a>
                </li>
                <li>{link.remarks}</li>
                <li className="count">{link.clicks.total}</li>
                <li className={`status-${status.toLowerCase().replace(' ', '-')}`}>
                  {status}
                </li>
                <li className="action-icons">
                  <button><img src="edit.png" alt="Edit" /></button>
                  <button><img src="delete.png" alt="Delete" /></button>
                </li>
              </ul>
            );
          })
        )}
      </div>

      {/* Pagination Controls */}
      <div className="pagination">
        {[...Array(Math.ceil(links.length / linksPerPage))].map((_, index) => (
          <button
            key={index + 1}
            onClick={() => handlePageChange(index + 1)}
            className={currentPage === index + 1 ? 'active' : ''}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
}

export default Data;
