import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Setting.css';

const Setting = () => {
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    phone: '',
  });
  const [updatedData, setUpdatedData] = useState({
    name: '',
    email: '',
    phone: '',
  });

  useEffect(() => {
    const token = localStorage.getItem('authToken'); // Correct key name is 'authToken'

    if (!token) {
      console.log('No token found, please log in');
      window.location.href = '/login'; // Redirect to login page if no token found
      return;
    }

    axios
      .get('http://localhost:4000/api/user', {
        headers: {
          'Authorization': `Bearer ${token}`, // Add token as Bearer token in Authorization header
        },
      })
      .then((response) => {
        if (response.data.user) {
          setUserData(response.data.user);
          setUpdatedData(response.data.user); // Set initial updatedData from fetched user
        } else {
          console.log('Error fetching user data');
        }
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  const handleSaveChanges = () => {
    const token = localStorage.getItem('authToken');
    
    // Prepare updated data (only fields that have changed)
    const dataToUpdate = {};
    if (updatedData.name && updatedData.name !== userData.name) {
      dataToUpdate.name = updatedData.name;
    }
    if (updatedData.email && updatedData.email !== userData.email) {
      dataToUpdate.email = updatedData.email;
    }
    if (updatedData.phone && updatedData.phone !== userData.phone) {
      dataToUpdate.phone = updatedData.phone;
    }

    if (Object.keys(dataToUpdate).length > 0) {
      axios
        .put('http://localhost:4000/api/user', dataToUpdate, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        })
        .then((response) => {
          if (response.status === 200) {
            setUserData({ ...userData, ...dataToUpdate });
            console.log('User data updated successfully');
          }
        })
        .catch((error) => {
          console.error('Error saving changes:', error);
        });
    } else {
      console.log('No changes detected');
    }
  };

  return (
    <div className="setting-container">
      <form className="setting-form">
        <div className="setting-field">
          <label className="setting-label">Name</label>
          <input
            className="setting-input"
            type="text"
            placeholder="Rahul Singh"
            value={updatedData.name}
            onChange={(e) => setUpdatedData({ ...updatedData, name: e.target.value })}
          />
        </div>
        <div className="setting-field">
          <label className="setting-label">Email id</label>
          <input
            className="setting-input"
            type="email"
            placeholder="rahulsingh@gmail.com"
            value={updatedData.email}
            onChange={(e) => setUpdatedData({ ...updatedData, email: e.target.value })}
          />
        </div>
        <div className="setting-field">
          <label className="setting-label">Mobile no.</label>
          <input
            className="setting-input"
            type="text"
            placeholder="1234567890"
            value={updatedData.phone}
            onChange={(e) => setUpdatedData({ ...updatedData, phone: e.target.value })}
          />
        </div>
        <button type="button" className="save-button" onClick={handleSaveChanges}>Save Changes</button>
      </form>
      <div className='delete-button-part'>
        <button type="button" className="delete-button">Delete Account</button>
      </div>
    </div>
  );
};

export default Setting;
