import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashpart.css";

function Dashpart() {
  const [totalClicks, setTotalClicks] = useState(0);
  const [deviceClicks, setDeviceClicks] = useState({
    desktop: 0,
    mobile: 0,
    tablet: 0,
  });
  const [dateWiseClicks, setDateWiseClicks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchClickStats = async () => {
      const token = localStorage.getItem("authToken"); // Assuming JWT token is in localStorage
      if (!token) {
        console.error("User is not authenticated");
        return;
      }

      try {
        const response = await axios.get("http://localhost:4000/api/get-clicks", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        // Assuming the API returns the following structure:
        // { totalClicks: number, deviceClicks: { desktop: number, mobile: number, tablet: number }, dateWiseClicks: [{ date: string, clicks: number }] }
        setTotalClicks(response.data.totalClicks);
        setDeviceClicks(response.data.deviceClicks);
        setDateWiseClicks(response.data.dateWiseClicks);
        setIsLoading(false);
      } catch (error) {
        console.error("Error fetching click stats:", error);
        setIsLoading(false);
      }
    };

    fetchClickStats();
  }, []);

  return (
    <div className="dashpart-container">
      <div className="click-stats-card">
        <h2>Total Clicks</h2>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <p className="click-count">{totalClicks}</p>
        )}
      </div>

      <div className="device-stats-card">
        <h2>Device-Wise Clicks</h2>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <ul>
            <li>Desktop: {deviceClicks.desktop}</li>
            <li>Mobile: {deviceClicks.mobile}</li>
            <li>Tablet: {deviceClicks.tablet}</li>
          </ul>
        )}
      </div>

      <div className="date-stats-card">
        <h2>Date-Wise Clicks</h2>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Clicks</th>
              </tr>
            </thead>
            <tbody>
              {dateWiseClicks.map((entry, index) => (
                <tr key={index}>
                  <td>{entry.date}</td>
                  <td>{entry.clicks}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Dashpart;
