import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Data.css";
import EditModal from "./EditModal";
import DeleteModal from "./DeleteModal"; // Import the DeleteModal component

function Data() {
  const [links, setLinks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedLink, setSelectedLink] = useState(null); // State for edit modal
  const [deleteLink, setDeleteLink] = useState(null); // State for delete modal
  const [currentPage, setCurrentPage] = useState(1);
  const linksPerPage = 7;

  // Fetch data
  useEffect(() => {
    const fetchLinks = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        console.error("User is not authenticated");
        return;
      }

      try {
        const response = await axios.get("http://localhost:4000/api/get-links", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setLinks(response.data.links);
        setIsLoading(false);
      } catch (error) {
        console.error("Error fetching links:", error);
      }
    };

    fetchLinks();
  }, []);

  const handleLinkUpdate = (updatedLink) => {
    const updatedLinks = links.map((link) =>
      link._id === updatedLink._id ? updatedLink : link
    );
    setLinks(updatedLinks);
  };

  // Handle delete confirmation
  const handleDeleteConfirm = async () => {
    if (!deleteLink) return;

    try {
      const response = await axios.delete(
        `http://localhost:4000/api/delete-link/${deleteLink._id}`
      );

      if (response.status === 200) {
        setLinks(links.filter((link) => link._id !== deleteLink._id));
        setDeleteLink(null); // Close modal after deletion
        alert("Link deleted successfully!");
      }
    } catch (error) {
      console.error("Error deleting link:", error);
    }
  };

  // Pagination
  const indexOfLastLink = currentPage * linksPerPage;
  const indexOfFirstLink = indexOfLastLink - linksPerPage;
  const currentLinks = links.slice(indexOfFirstLink, indexOfLastLink);

  return (
    <div className="data-container">
      <div className="table-header">
        <ul className="data-table">
          <li>Date</li>
          <li>Original Link</li>
          <li>Short Link</li>
          <li>Remarks</li>
          <li>Clicks</li>
          <li>Status</li>
          <li>Action</li>
        </ul>
      </div>

      <div className="table-body">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          currentLinks.map((link, index) => (
            <ul className="data-row" key={index}>
              <li>{new Date(link.createdAt).toLocaleString()}</li>
              <li>{link.destinationUrl.slice(0, 10)}...</li>
              <li>{link.shortUrl.slice(0, 10)}...</li>
              <li>{link.remarks}</li>
              <li>{link.clicks.total}</li>
              <li>{link.expiryDate ? "Active" : "Inactive"}</li>
              <li className="action-icons">
                <button
                  className="edit"
                  onClick={() => setSelectedLink(link)}
                >
                  <img src="edit.png" alt="Edit" />
                </button>
                <button
                  className="delete"
                  onClick={() => setDeleteLink(link)}
                >
                  <img src="delete.png" alt="Delete" />
                </button>
              </li>
            </ul>
          ))
        )}
      </div>

      <div className="pagination">
        {Array.from({ length: Math.ceil(links.length / linksPerPage) }, (_, i) => i + 1).map(
          (page) => (
            <button
              key={page}
              onClick={() => setCurrentPage(page)}
              className={currentPage === page ? "active" : ""}
            >
              {page}
            </button>
          )
        )}
      </div>

      {/* Edit Modal */}
      <EditModal
        isOpen={!!selectedLink}
        onClose={() => setSelectedLink(null)}
        link={selectedLink}
        onUpdate={handleLinkUpdate}
      />

      {/* Delete Modal */}
      {deleteLink && (
        <DeleteModal
          onClose={() => setDeleteLink(null)}
          onConfirm={handleDeleteConfirm}
        />
      )}
    </div>
  );
}

export default Data;
